package mixinnet

import (
	"context"
	"testing"

	"github.com/stretchr/testify/require"
)

func TestTransactionFromRaw(t *testing.T) {
	t.Run("legacy-network", func(t *testing.T) {
		ctx := context.Background()
		client := NewClient(DefaultLegacyConfig)

		t.Run("mint-tx", func(t *testing.T) {
			tx, err := TransactionFromRaw("")
			require.Nil(t, err)

			hash, err := HashFromString("0ade64d9f5640522ecabdf8954c3b0e1f0260718ccbee12d8d02c33f587558a7")
			require.Nil(t, err)

			hash1, err := tx.TransactionHash()
			require.Nil(t, err)
			require.Equal(t, hash[:], hash1[:], "hash not matched: %v != %v", hash, hash1)
		})

		t.Run("pledge-tx", func(t *testing.T) {
			tx, err := TransactionFromRaw("")
			require.Nil(t, err)

			hash, err := HashFromString("abadfce0377eae4e0057289ddcd067068171f034814c476a0d2c4a7807f222a7")
			require.Nil(t, err)

			hash1, err := tx.TransactionHash()
			require.Nil(t, err)
			require.Equal(t, hash[:], hash1[:], "hash not matched: %v != %v", hash, hash1)

			sourceTx, err := client.GetTransaction(ctx, *tx.Inputs[0].Hash)
			require.Nil(t, err)
			require.NotNil(t, sourceTx)

			output := sourceTx.Outputs[int(tx.Inputs[0].Index)]
			require.Equal(t, 1, len(output.Keys))

			require.NotNil(t, tx.AggregatedSignature)
			require.True(t, len(tx.AggregatedSignature.Signers) == 1 && tx.AggregatedSignature.Signers[0] == 0)
			require.NotNil(t, tx.AggregatedSignature.Signature)

			pubSpend, err := KeyFromString("b27b4a84b6627c77a57074db780d16968e902614a99f02dbc5267ddfaaf38935")
			require.Nil(t, err)
			addr := AddressFromPublicSpend(pubSpend)
			verified, err := client.VerifyTransaction(ctx, addr, hash)
			require.Nil(t, err)
			require.True(t, verified)

			ghostPub := DeriveGhostPublicKey(sourceTx.Version, &addr.PrivateViewKey, &output.Mask, &addr.PublicSpendKey, 0)
			require.Equal(t, output.Keys[0][:], ghostPub[:])

			payload, err := tx.DumpPayload()
			require.Nil(t, err)
			require.True(t, ghostPub.Verify(payload, *tx.AggregatedSignature.Signature))
		})
	})

	t.Run("safe-network", func(t *testing.T) {
		ctx := context.Background()
		client := NewClient(DefaultSafeConfig)

		t.Run("mint-tx", func(t *testing.T) {
			tx, err := TransactionFromRaw("")
			require.Nil(t, err)

			hash, err := HashFromString("eb3cd0b4cfb257e0ef5f48ca17662b3712c683113e4d46c9b4ba14663310df04")
			require.Nil(t, err)

			hash1, err := tx.TransactionHash()
			require.Nil(t, err)
			require.Equal(t, hash[:], hash1[:], "hash not matched: %v != %v", hash, hash1)
		})

		t.Run("pledge-tx", func(t *testing.T) {
			tx, err := TransactionFromRaw("")
			require.Nil(t, err)

			hash, err := HashFromString("cb91aab70e6af785aba49482bb033bb53f0b038594712d7ba03576df025e5c32")
			require.Nil(t, err)

			hash1, err := tx.TransactionHash()
			require.Nil(t, err)
			require.Equal(t, hash[:], hash1[:], "hash not matched: %v != %v", hash, hash1)

			sourceTx, err := client.GetTransaction(ctx, *tx.Inputs[0].Hash)
			require.Nil(t, err)
			require.NotNil(t, sourceTx)

			output := sourceTx.Outputs[int(tx.Inputs[0].Index)]
			require.Equal(t, 1, len(output.Keys))

			require.NotNil(t, tx.AggregatedSignature)
			require.True(t, len(tx.AggregatedSignature.Signers) == 1 && tx.AggregatedSignature.Signers[0] == 0)
			require.NotNil(t, tx.AggregatedSignature.Signature)

			pubSpend, err := KeyFromString("9c43eb7377cfcf0c4ab2239d309b75cb25a791f8b7fdc902b4d7c57c28ef64a7")
			require.Nil(t, err)
			addr := AddressFromPublicSpend(pubSpend)
			verified, err := client.VerifyTransaction(ctx, addr, hash)
			require.Nil(t, err)
			require.True(t, verified)

			ghostPub := DeriveGhostPublicKey(tx.Version, &addr.PrivateViewKey, &output.Mask, &addr.PublicSpendKey, 0)
			require.Equal(t, output.Keys[0][:], ghostPub[:])

			require.True(t, ghostPub.VerifyHash(*tx.Hash, *tx.AggregatedSignature.Signature))
		})

		t.Run("normal-tx-receiver", func(t *testing.T) {
			tx, err := TransactionFromRaw("")
			require.Nil(t, err)

			require.Equal(t, 1, len(tx.Outputs))
			output := tx.Outputs[0]
			pubSpend, err := KeyFromString("921c1ca5927ca9b837a0415f7e825b1c13a4b02a4cbb98b7d26d9a171fff48b8")
			require.Nil(t, err)

			addr := AddressFromPublicSpend(pubSpend)
			ghostPub := DeriveGhostPublicKey(tx.Version, &addr.PrivateViewKey, &output.Mask, &addr.PublicSpendKey, 0)
			require.Equal(t, output.Keys[0][:], ghostPub[:])
		})
	})
}
